create procedure dbo.work_item_findbysubject
(
	@rowStart	int,
	@rowEnd		int,
	@subject	nvarchar(500)
)
as

Select a.* From (Select ROW_NUMBER() OVER (ORDER BY SubmittedAnswerId) AS RowNum, * From work_item Where SubmittedAnswerId = @submittedanswerId
) as a Where RowNum > @rowStart AND RowNum <= @rowEnd
Select count(1) as TotalRecords from work_item where SubmittedAnswerId = @submittedanswerId

create procedure dbo.work_item_findbysubmitted_answerId
(
	@rowStart	int,
	@rowEnd		int,
	@submittedanswerId	int
)
as

Select a.* From (Select ROW_NUMBER() OVER (ORDER BY SubmittedAnswerId) AS RowNum, * From work_item Where UserId = @userId
) as a Where RowNum > @rowStart AND RowNum <= @rowEnd
Select count(1) as TotalRecords from work_item where UserId = @userId

create procedure dbo.work_item_findbyuser
(
	@rowStart	int,
	@rowEnd		int,
	@userId	int
)
as

INSERT INTO work_item(	
