	Correct,
	Progress,
	UserId,
	ExerciseId,
	Difficulty,
	[Subject],
	[Domain],
	[LearningObjective]
	)
 VALUES(
	@SubmittedAnswerId,
	@SubmitDateTime	,
	@Correct,
	@Progress,
	@UserId,
	@ExerciseId,
	@Difficulty,
	@Subject,
	@Domain,
	@LearningObjective 
	)

 
